import {
  GET_CUSTOMER_REQUEST,
  GET_CUSTOMER_SUCCESS,
  GET_CUSTOMER_FAIL,
  CUSTOMER_ADD_REQUEST,
  CUSTOMER_ADD_SUCCESS,
  CUSTOMER_ADD_FAIL,
  CUSTOMER_DELETE_REQUEST,
  CUSTOMER_DELETE_SUCCESS,
  CUSTOMER_DELETE_FAIL,
} from '../constants/customerConstant';

export const listCustomerReducer = (state = { customers: [] }, action) => {
  const { type, payload } = action;
  switch (type) {
    case GET_CUSTOMER_REQUEST:
      return { loading: true, customers: [] };
    case GET_CUSTOMER_SUCCESS:
      return { loading: false, customers: payload };
    case GET_CUSTOMER_FAIL:
      return { loading: false, error: payload };
    default:
      return state;
  }
};

export const addCustomerReducer = (state = {}, action) => {
  const { type, payload } = action;
  switch (type) {
    case CUSTOMER_ADD_REQUEST:
      return { loading: true, ...state };
    case CUSTOMER_ADD_SUCCESS:
      return {
        loading: false,
        success: true,
      };
    case CUSTOMER_ADD_FAIL:
      return { loading: false, error: payload };
    default:
      return state;
  }
};

export const customerReducer = (state = {}, action) => {
  const { type, payload } = action;
  switch (type) {
    case CUSTOMER_DELETE_REQUEST:
    case CUSTOMER_ADD_REQUEST:
      return { loading: true, ...state };
    case CUSTOMER_ADD_SUCCESS:
      return {
        loading: false,
        success: true,
      };
    case CUSTOMER_DELETE_SUCCESS:
      return {
        loading: false,
        success: true,
      };
    case CUSTOMER_DELETE_FAIL:
    case CUSTOMER_ADD_FAIL:
      return { loading: false, error: payload };
    default:
      return state;
  }
};
