import React, { useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { deleteCustomerToList, getCustomers } from '../actions/customerAction';

const CustomerList = () => {
  const dispatch = useDispatch();

  const customer = useSelector((state) => state.customer);
  const { loading, success } = customer;

  const listCustomer = useSelector((state) => state.listCustomer);
  const { loading: listCustomerLoading, customers: listCustomers } =
    listCustomer;

  useEffect(() => {
    dispatch(getCustomers());
  }, [loading, success, customers, dispatch]);

  const deleteHandler = (id) => {
    dispatch(deleteCustomerToList(id));
  };

  return listCustomerLoading ? (
    <h1>loading ... </h1>
  ) : (
    <table className="u-full-width">
      <thead>
        <tr>
          <th>Name</th>
          <th>Email</th>
          <th>Age</th>
          <th>Location</th>
          <th></th>
        </tr>
      </thead>
      {listCustomers.map((customer) => (
        <tbody key={customer._id}>
          <tr>
            <td>{customer.name} </td>
            <td>{customer.email} </td>
            <td>{customer.age} </td>
            <td>{customer.location} </td>
            <td
              className="delete-button"
              onClick={() => deleteHandler(customer._id)}
            >
              X
            </td>
          </tr>
        </tbody>
      ))}
    </table>
  );
};

export default CustomerList;
