import { ALERT_SUCCESS, ALERT_DELETE } from '../constants/alertConstant';
import {
  GET_CUSTOMER_REQUEST,
  GET_CUSTOMER_SUCCESS,
  GET_CUSTOMER_FAIL,
  CUSTOMER_ADD_SUCCESS,
  CUSTOMER_ADD_REQUEST,
  CUSTOMER_ADD_FAIL,
  CUSTOMER_DELETE_SUCCESS,
  CUSTOMER_DELETE_REQUEST,
  CUSTOMER_DELETE_FAIL,
} from '../constants/customerConstant';
import axios from 'axios';

export const getCustomers = () => async (dispatch) => {
  try {
    dispatch({
      type: GET_CUSTOMER_REQUEST,
    });
    const { data } = await axios.get('/api/customer');

    dispatch({
      type: GET_CUSTOMER_SUCCESS,
      payload: data,
    });
  } catch (error) {
    dispatch({
      type: GET_CUSTOMER_FAIL,
      payload:
        error.response && error.response.data.message
          ? error.response.data.message
          : error.message,
    });
  }
};

export const addCustomerToList = (newCustomer) => async (dispatch) => {
  try {
    dispatch({
      type: CUSTOMER_ADD_REQUEST,
    });

    const config = {
      headers: {
        'Content-Type': 'application/json',
      },
    };

    const { data } = await axios.post(
      '/api/customer',
      {
        name: newCustomer.name,
        email: newCustomer.email,
        location: newCustomer.location,
        age: newCustomer.age,
      },
      config
    );

    dispatch({
      type: CUSTOMER_ADD_SUCCESS,
      payload: data,
    });
  } catch (error) {
    dispatch({
      type: CUSTOMER_ADD_FAIL,
      payload:
        error.response && error.response.data.message
          ? error.response.data.message
          : error.message,
    });
  }
};

export const deleteCustomerToList = (id) => async (dispatch) => {
  try {
    dispatch({
      type: CUSTOMER_DELETE_REQUEST,
    });

    const config = {
      headers: {
        'Content-Type': 'application/json',
      },
    };

    await axios.delete(`/api/customer/${id}`, config);

    dispatch({
      type: CUSTOMER_DELETE_SUCCESS,
    });

    dispatch({
      type: ALERT_SUCCESS,
      payload: { alertType: 'delete', msg: 'customer deleted' },
    });

    setTimeout(() => {
      dispatch({ type: ALERT_DELETE });
    }, 3000);
  } catch (error) {
    dispatch({
      type: CUSTOMER_DELETE_FAIL,
      payload:
        error.response && error.response.data.message
          ? error.response.data.message
          : error.message,
    });
  }
};
