import React, { useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { deleteCustomerToList } from '../actions/customerAction';

const CustomerList = ({ deleteHandler }) => {
  const dispatch = useDispatch();
  const addCustomer = useSelector((state) => state.addCustomer);
  const { loading, success, customer: customers } = addCustomer;

  useEffect(() => {}, [loading, success, customers]);

  return (
    <table className="u-full-width">
      <thead>
        <tr>
          <th>Name</th>
          <th>Email</th>
          <th>Age</th>
          <th>Location</th>
          <th></th>
        </tr>
      </thead>
      {customers.map((customer) => (
        <tbody key={customer.id}>
          <tr>
            <td>{customer.name} </td>
            <td>{customer.email} </td>
            <td>{customer.age} </td>
            <td>{customer.location} </td>
            <td
              className="delete-button"
              onClick={() =>
                dispatch(deleteCustomerToList(customers, customer.id))
              }
            >
              X
            </td>
          </tr>
        </tbody>
      ))}
    </table>
  );
};

export default CustomerList;
