import {
  CUSTOMER_ADD_REQUEST,
  CUSTOMER_ADD_SUCCESS,
  CUSTOMER_ADD_FAIL,
  CUSTOMER_DELETE_REQUEST,
  CUSTOMER_DELETE_SUCCESS,
  CUSTOMER_DELETE_FAIL,
} from '../constants/customerConstant';

export const addCustomerReducer = (state = {}, action) => {
  const { type, payload } = action;
  switch (type) {
    case CUSTOMER_ADD_REQUEST:
      return { loading: true };
    case CUSTOMER_ADD_SUCCESS:
      return { loading: false, success: true, customer: [...state, payload] };
    case CUSTOMER_ADD_FAIL:
      return { loading: false, error: payload };
    default:
      return state;
  }
};

export const deleteCustomerReducer = (state = {}, action) => {
  switch (action.type) {
    case CUSTOMER_DELETE_REQUEST:
      return state;
    case CUSTOMER_DELETE_SUCCESS:
      return state;
    case CUSTOMER_DELETE_FAIL:
      return state;
    default:
      return state;
  }
};
