import React, { useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { deleteCustomerToList, getCustomers } from '../actions/customerAction';

const CustomerList = () => {
  const dispatch = useDispatch();

  const addCustomer = useSelector((state) => state.addCustomer);
  const { loading, success } = addCustomer;

  const deleteCustomer = useSelector((state) => state.deleteCustomer);
  const { loading: deleteLoading, success: deleteSuccess } = deleteCustomer;

  const listCustomer = useSelector((state) => state.listCustomer);
  const {
    loading: listCustomerLoading,
    success: getSuccess,
    customers: listCustomers,
  } = listCustomer;

  const deleteHandler = (e, id) => {
    e.preventDefault();
    dispatch(deleteCustomerToList(id));
  };

  useEffect(() => {
    dispatch(getCustomers());
  }, [loading, success, deleteLoading, deleteSuccess, dispatch]);

  return listCustomerLoading ? (
    <h1>loading ... </h1>
  ) : (
    <table className="u-full-width">
      <thead>
        <tr>
          <th>Name</th>
          <th>Email</th>
          <th>Age</th>
          <th>Location</th>
          <th></th>
        </tr>
      </thead>
      {listCustomers.map((customer) => (
        <tbody key={customer._id}>
          <tr>
            <td>{customer.name} </td>
            <td>{customer.email} </td>
            <td>{customer.age} </td>
            <td>{customer.location} </td>
            <td
              className="delete-button"
              onClick={(e) => deleteHandler(e, customer._id)}
            >
              X
            </td>
          </tr>
        </tbody>
      ))}
    </table>
  );
};

export default CustomerList;
